using System;using UnityEngine;public class ChangeVariableTags : MonoBehaviour, ITag {    public void Calling(string value)    {        DialogueController dialogueController = GetComponent<DialogueController>();        value = value.Trim('(', ')');        string[] parts = value.Split('=');        string nameVariable;        string valueVariable;        if (parts.Length == 2)        {            nameVariable = parts[0].Trim();             valueVariable = parts[1].Trim();        }        else        {            Debug.Log("Неверный формат тега (name = value)");            return;        }                dialogueController.Collocutor.ChangeInkVariableInUnity(nameVariable, Boolean.Parse(valueVariable));    }}