using System.Linq;using UnityEngine;public class ItemTags : MonoBehaviour, ITag{    [SerializeField] private Player _player;    private Inventory _inventory;    private void Awake()    {        _inventory = _player.Inventory;    }    public void Calling(string value)    {        ItemInfo itemForTransfer = Resources.LoadAll<ItemInfo>("ScriptableObject").First((info) => info.id == value);        _inventory.AddItem(itemForTransfer);    }}