using UnityEngine;public class MotherNPC : Npc{    [SerializeField] private Player _player;    private Inventory _inventory;    public override void ChangeInkVariableInUnity(string nameVariable, bool value) {}    protected override void SetInkVariable()    {        _inventory = _player.Inventory;        foreach (Item item in _inventory.items)        {            if (item.itemInfo.id == "bread")            {                _story.variablesState["playerHaveBread"] = true;                _inventory.RemoveItem(item.itemInfo);                break;            }        }    }}